pub enum Error {
    IoError(FmtError),
    UnsupportedScalar(crate::ScalarKind, crate::Bytes),
    Unimplemented(String),
    Custom(String),
}

pub enum BadIntError {
    ParseIntError(ParseIntError),
    NegativeZero,
    LeadingZeros,
}

pub enum ResolveError {
    OutOfBoundsIndex {
        expr: Handle<crate::Expression>,
        index: u32,
    },
    InvalidAccess {
        expr: Handle<crate::Expression>,
        indexed: bool,
    },
    InvalidSubAccess {
        ty: Handle<crate::Type>,
        indexed: bool,
    },
    InvalidScalar(Handle<crate::Expression>),
    InvalidVector(Handle<crate::Expression>),
    InvalidPointer(Handle<crate::Expression>),
    InvalidImage(Handle<crate::Expression>),
    FunctionNotDefined {
        name: String,
    },
    FunctionReturnsVoid,
    TypeNotFound,
    IncompatibleOperands(String),
}

pub enum UniformityDisruptor {
    Expression(Handle<crate::Expression>),
    Return,
    Discard,
}

pub enum ComposeError {
    TypeDoesntExist(Handle<crate::Type>),
    Type(Handle<crate::Type>),
    ComponentCount {
        given: u32,
        expected: u32,
    },
    ComponentType {
        index: u32,
    },
}

pub enum CallError {
    InvalidFunction,
    ForwardDeclaredFunction,
    Argument {
        index: usize,
        error: ExpressionError,
    },
    ResultAlreadyInScope(Handle<crate::Expression>),
    ResultValue(ExpressionError),
    ArgumentCount {
        required: usize,
        seen: usize,
    },
    ArgumentType {
        index: usize,
        required: Handle<crate::Type>,
        seen_expression: Handle<crate::Expression>,
    },
    ExpressionMismatch(Option<Handle<crate::Expression>>),
}

pub enum FunctionError {
    Expression {
        handle: Handle<crate::Expression>,
        error: ExpressionError,
    },
    ExpressionAlreadyInScope(Handle<crate::Expression>),
    LocalVariable {
        handle: Handle<crate::LocalVariable>,
        name: String,
        error: LocalVariableError,
    },
    InvalidArgumentType {
        index: usize,
        name: String,
    },
    InvalidArgumentPointerClass {
        index: usize,
        name: String,
        class: crate::StorageClass,
    },
    InstructionsAfterReturn,
    BreakOutsideOfLoopOrSwitch,
    ContinueOutsideOfLoop,
    InvalidReturnSpot,
    InvalidReturnType(Option<Handle<crate::Expression>>),
    InvalidIfType(Handle<crate::Expression>),
    InvalidSwitchType(Handle<crate::Expression>),
    ConflictingSwitchCase(i32),
    InvalidStorePointer(Handle<crate::Expression>),
    InvalidStoreValue(Handle<crate::Expression>),
    InvalidStoreTypes {
        pointer: Handle<crate::Expression>,
        value: Handle<crate::Expression>,
    },
    InvalidExpression(Handle<crate::Expression>),
    InvalidImageStore(ExpressionError),
    InvalidCall {
        function: Handle<crate::Function>,
        error: CallError,
    },
    InvalidAtomic(AtomicError),
    NonUniformControlFlow(UniformityRequirements, Handle<crate::Expression>,
                          UniformityDisruptor),
}

pub enum GlobalVariableError {
    InvalidUsage,
    InvalidType,
    MissingTypeFlags {
        required: super::TypeFlags,
        seen: super::TypeFlags,
    },
    UnsupportedCapability(Capabilities),
    InvalidBinding,
    Alignment(Handle<crate::Type>, Disalignment),
}

pub enum Disalignment {
    ArrayStride {
        stride: u32,
        alignment: u32,
    },
    StructSpan {
        span: u32,
        alignment: u32,
    },
    MemberOffset {
        index: u32,
        offset: u32,
        alignment: u32,
    },
    UnsizedMember {
        index: u32,
    },
    NonHostShareable,
}

pub enum TypeError {
    InvalidWidth(crate::ScalarKind, crate::Bytes),
    InvalidAtomicWidth(crate::ScalarKind, crate::Bytes),
    UnresolvedBase(Handle<crate::Type>),
    InvalidPointerBase(Handle<crate::Type>),
    InvalidData(Handle<crate::Type>),
    InvalidArrayBaseType(Handle<crate::Type>),
    InvalidArraySizeConstant(Handle<crate::Constant>),
    NonPositiveArrayLength(Handle<crate::Constant>),
    InsufficientArrayStride {
        stride: u32,
        base_size: u32,
    },
    InvalidDynamicArray(String, Handle<crate::Type>),
    MemberOverlap {
        index: u32,
        offset: u32,
    },
    MemberOutOfBounds {
        index: u32,
        offset: u32,
        size: u32,
        span: u32,
    },
    NestedTopLevel,
}

pub enum ConstantError {
    InvalidType,
    UnresolvedComponent(Handle<crate::Constant>),
    UnresolvedSize(Handle<crate::Constant>),
    Compose(ComposeError),
}

pub enum ValidationError {
    Layouter(InvalidBaseType),
    Type {
        handle: Handle<crate::Type>,
        name: String,
        error: TypeError,
    },
    Constant {
        handle: Handle<crate::Constant>,
        name: String,
        error: ConstantError,
    },
    GlobalVariable {
        handle: Handle<crate::GlobalVariable>,
        name: String,
        error: GlobalVariableError,
    },
    Function {
        handle: Handle<crate::Function>,
        name: String,
        error: FunctionError,
    },
    EntryPoint {
        stage: crate::ShaderStage,
        name: String,
        error: EntryPointError,
    },
    Corrupted,
}

pub enum StorageClass {
    Function,
    Private,
    WorkGroup,
    Uniform,
    Storage {
        access: StorageAccess,
    },
    Handle,
    PushConstant,
}

pub enum ImageClass {
    Sampled {
        kind: ScalarKind,
        multi: bool,
    },
    Depth {
        multi: bool,
    },
    Storage {
        format: StorageFormat,
        access: StorageAccess,
    },
}

pub enum TypeInner {
    Scalar {
        kind: ScalarKind,
        width: Bytes,
    },
    Vector {
        size: VectorSize,
        kind: ScalarKind,
        width: Bytes,
    },
    Matrix {
        columns: VectorSize,
        rows: VectorSize,
        width: Bytes,
    },
    Atomic {
        kind: ScalarKind,
        width: Bytes,
    },
    Pointer {
        base: Handle<Type>,
        class: StorageClass,
    },
    ValuePointer {
        size: Option<VectorSize>,
        kind: ScalarKind,
        width: Bytes,
        class: StorageClass,
    },
    Array {
        base: Handle<Type>,
        size: ArraySize,
        stride: u32,
    },
    Struct {
        top_level: bool,
        members: Vec<StructMember>,
        span: u32,
    },
    Image {
        dim: ImageDimension,
        arrayed: bool,
        class: ImageClass,
    },
    Sampler {
        comparison: bool,
    },
}

pub enum ConstantInner {
    Scalar {
        width: Bytes,
        value: ScalarValue,
    },
    Composite {
        ty: Handle<Type>,
        components: Vec<Handle<Constant>>,
    },
}

pub enum Binding {
    BuiltIn(BuiltIn),
    Location {
        location: u32,
        interpolation: Option<Interpolation>,
        sampling: Option<Sampling>,
    },
}

pub enum AtomicFunction {
    Add,
    Subtract,
    And,
    ExclusiveOr,
    InclusiveOr,
    Min,
    Max,
    Exchange {
        compare: Option<Handle<Expression>>,
    },
}

pub enum SampleLevel {
    Auto,
    Zero,
    Exact(Handle<Expression>),
    Bias(Handle<Expression>),
    Gradient {
        x: Handle<Expression>,
        y: Handle<Expression>,
    },
}

pub enum ImageQuery {
    Size {
        level: Option<Handle<Expression>>,
    },
    NumLevels,
    NumLayers,
    NumSamples,
}

pub enum Expression {
    Access {
        base: Handle<Expression>,
        index: Handle<Expression>,
    },
    AccessIndex {
        base: Handle<Expression>,
        index: u32,
    },
    Constant(Handle<Constant>),
    Splat {
        size: VectorSize,
        value: Handle<Expression>,
    },
    Swizzle {
        size: VectorSize,
        vector: Handle<Expression>,
        pattern: [SwizzleComponent; ],
    },
    Compose {
        ty: Handle<Type>,
        components: Vec<Handle<Expression>>,
    },
    FunctionArgument(u32),
    GlobalVariable(Handle<GlobalVariable>),
    LocalVariable(Handle<LocalVariable>),
    Load {
        pointer: Handle<Expression>,
    },
    ImageSample {
        image: Handle<Expression>,
        sampler: Handle<Expression>,
        coordinate: Handle<Expression>,
        array_index: Option<Handle<Expression>>,
        offset: Option<Handle<Constant>>,
        level: SampleLevel,
        depth_ref: Option<Handle<Expression>>,
    },
    ImageLoad {
        image: Handle<Expression>,
        coordinate: Handle<Expression>,
        array_index: Option<Handle<Expression>>,
        index: Option<Handle<Expression>>,
    },
    ImageQuery {
        image: Handle<Expression>,
        query: ImageQuery,
    },
    Unary {
        op: UnaryOperator,
        expr: Handle<Expression>,
    },
    Binary {
        op: BinaryOperator,
        left: Handle<Expression>,
        right: Handle<Expression>,
    },
    Select {
        condition: Handle<Expression>,
        accept: Handle<Expression>,
        reject: Handle<Expression>,
    },
    Derivative {
        axis: DerivativeAxis,
        expr: Handle<Expression>,
    },
    Relational {
        fun: RelationalFunction,
        argument: Handle<Expression>,
    },
    Math {
        fun: MathFunction,
        arg: Handle<Expression>,
        arg1: Option<Handle<Expression>>,
        arg2: Option<Handle<Expression>>,
        arg3: Option<Handle<Expression>>,
    },
    As {
        expr: Handle<Expression>,
        kind: ScalarKind,
        convert: Option<Bytes>,
    },
    CallResult(Handle<Function>),
    AtomicResult {
        kind: ScalarKind,
        width: Bytes,
        comparison: bool,
    },
    ArrayLength(Handle<Expression>),
}

pub enum Statement {
    Emit(Range<Expression>),
    Block(Block),
    If {
        condition: Handle<Expression>,
        accept: Block,
        reject: Block,
    },
    Switch {
        selector: Handle<Expression>,
        cases: Vec<SwitchCase>,
        default: Block,
    },
    Loop {
        body: Block,
        continuing: Block,
    },
    Break,
    Continue,
    Return {
        value: Option<Handle<Expression>>,
    },
    Kill,
    Barrier(Barrier),
    Store {
        pointer: Handle<Expression>,
        value: Handle<Expression>,
    },
    ImageStore {
        image: Handle<Expression>,
        coordinate: Handle<Expression>,
        array_index: Option<Handle<Expression>>,
        value: Handle<Expression>,
    },
    Atomic {
        pointer: Handle<Expression>,
        fun: AtomicFunction,
        value: Handle<Expression>,
        result: Handle<Expression>,
    },
    Call {
        function: Handle<Function>,
        arguments: Vec<Handle<Expression>>,
        result: Option<Handle<Expression>>,
    },
}
